(window.webpackJsonp=window.webpackJsonp||[]).push([[25],{405:function(n,h,t){"use strict";t.r(h);var c=t(25),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h2",{attrs:{id:"y-kien-nguoi-dung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#y-kien-nguoi-dung"}},[n._v("#")]),n._v(" Ý kiến người dùng")]),n._v(" "),t("p",[n._v("Trong hơn 10 năm phát triển, phần mềm QLCL GXD đã nhận được nhiều lời khen, cổ vũ, động viên của các đồng nghiệp. Điều này tiếp sức rất nhiều cho teams phát triển. Chúng tôi xin chia sẻ lại với các đồng nghiệp để yên tâm và chung vui. Việc bạn chia sẻ các câu chuyện ứng dụng phần mềm QLCL GXD tới mọi người cũng là giúp phần mềm mạnh lên, có kinh phí để cải tiến và bạn là người được hưởng lợi.")]),n._v(" "),t("p",[n._v("Chuyện của Kỹ sư Vũ Giang, Công ty Cầu 11, Tổng Cty XD Thăng Long trên một công trường thi công công trình cầu: "),t("strong",[t("em",[n._v("Em làm quản lý ở một đơn vị, công tác quản lý đã rất bận, mà em vẫn phải dành thời gian nhiều để hướng dẫn mấy anh em kỹ thuật về công việc này (làm hồ sơ nghiệm thu, hoàn công…) Bạn nào làm nhanh còn đỡ, chứ không thì mất thời gian, mất tiền in ấn hồ sơ, công sức làm lại, chỉnh sửa…")])]),n._v(".")]),n._v(" "),t("p",[n._v("Ý kiến của Kỹ sư Trịnh Thế Quyết, Công ty CP Xây dựng và Đầu tư Hà Nội, Công ty cổ phần tư vấn thiết kế và đầu tư xây dựng Hà Thành: "),t("strong",[t("em",[n._v("Trong thời gian tới đây mọi kỹ sư thi công sẽ nhàn đi bởi phần mềm QLXD GXD với những tính năng nổi bật. Nếu ai đã làm nghiệm thu tôi tin chắc là không cần đọc hướng dẫn vẫn có thể sử dụng được. Theo cảm nhận của tôi thì phần mềm có những tính năng rất hay: Tùy biến mọi văn bản nghiệm thu; Quản lý được sản phẩm vật liệu đầu vào đầu ra công trình…")])]),n._v(".")]),n._v(" "),t("p",[n._v("Ý kiến của các kỹ sư trên công trường xây dựng một công trình thuộc dự án Tổ hợp chung cư cao tầng và dịch vụ N04 của Tổng Cty đầu tư phát triển hạ tầng đô thị UDIC: "),t("strong",[t("em",[n._v("Phần mềm rất tốt! quản lý một cách chuyên nghiệp và khoa học, nếu như mà ứng dụng ngay từ đầu vào công trình thì tốt biết mấy! Các công trình mới khởi công nên tính chuyện ứng dụng ngay, còn các công trình dang dở thì chịu khó đưa số liệu cũ vào để quản lý")])]),n._v(".")]),n._v(" "),t("p",[n._v("Kỹ sư Lê Ngọc Ánh, Tư vấn giám sát trên công trường Alumin Nhân Cơ nhận xét: "),t("strong",[t("em",[n._v('Phần mềm này ai làm hồ sơ chất lượng rồi chả cần đọc hướng dẫn cũng làm được, rất dễ dùng. Tội nghiệp các Nhà thầu quá, nguyên nửa đội bóng mà làm hồ sơ chất lượng rất chật vật, có phần mềm này thì "chấp" cả đội, chỉ cần 1 người chuyên quản lý là đủ, rất hiệu quả')])]),n._v(".")]),n._v(" "),t("p",[n._v("Kỹ sư Nguyễn Văn Tuấn, Công ty TNHH MTV Hoàng Anh Đạt, Bắc Giang nói: "),t("strong",[t("em",[n._v("Phải dùng phần mềm mới thấy hiệu quả tuyệt vời của nó. Số tiền mua khóa của nó quá rẻ so với kết quả mình có được, nếu xét chi tiết về hiệu quả công việc thì giá mua gần như cho không. Cảm ơn GXD!")])])]),n._v(" "),t("p",[n._v("Kỹ sư Kiều Linh, Công ty CP Cao Ốc Vàng, Ba Vì, Hà Nội: "),t("strong",[t("em",[n._v("Từ Version 1 đến Version 3, em đều đã dùng để trực tiếp làm hồ sơ công trình. Cảm ơn đội ngũ kỹ sư GXD rất nhiều. Em nhận thấy rằng giá của sản phẩm quá rẻ so với những gì mà em nhận được.Với em, Version 4 là rất hoàn hảo rồi (Ngoài sức tưởng tượng rồi)")])]),n._v(".")]),n._v(" "),t("p",[n._v("Kỹ sư Ngô Minh Cường: "),t("strong",[t("em",[n._v("Mấy hôm nay không có quả phần mềm QLCL GXD này thì gay anh ạ. Hồ sơ chất lượng công trình bây giờ là muỗi. Trước kia em cần 5 kỹ sư để làm hồ sơ chất lượng 1 công trình mà không xong. Giờ 1 kỹ sư làm hồ sơ chất lượng 5 công trình thỏa mái luôn. Công việc lâu dài, em đầu tư luôn quả khóa cứng để dùng, rất tiện lợi và quá rẻ!")])])]),n._v(" "),t("p",[n._v("Dù là kỹ sư trẻ chưa có kinh nghiệm hay là người dày dạn kinh nghiệm,bạn cũng cần có một công cụ hỗ trợ làm việc thuận tiện, phù hợp và tự động. Hướng dẫn này sẽ cung cấp hệ thống những khái niệm cơ bản, hướng dẫn nghiệp vụ lập hồ sơ nghiệm thu, hồ sơ chất lượng, hồ sơ hoàn công, rút ngắn công đoạn tiếp cận công việc, tiết kiệm công sức và thời gian làm việc. Giảm tải cho cả kỹ sư và sếp.")]),n._v(" "),t("p",[n._v("Tài liệu này sử dụng với phần mềm Quản lý chất lượng GXD (QLCL GXD) là tư liệu tham khảo tốt cho công tác quản lý chất lượng công trình, làm công cụ huấn luyện nhân viên. Hãy chuyển tới tay các kỹ sư, nhân viên của bạn nghiên cứu, tìm hiểu và thực hành theo phần mềm. Đừng ngại ghi chú thêm các thắc mắc, các phát hiện của bạn vào tài liệu để nâng cao nghiệp vụ.")])])}),[],!1,null,null,null);h.default=i.exports}}]);